mapper.select:
SELECT * FROM `coupon`


        `user_id` IN

            #{id}



        `coupon_type` = #{args.couponType}


        `coupon_state` = #{args.couponState}


        AND `create_date` BETWEEN #{args.startDate} AND DATE_ADD(#{args.endDate},INTERVAL 1 DAY)

    AND deleted = 0

ORDER BY `create_date` DESC
