mapper.select:
SELECT * FROM `order`

    `user_id` = #{userId}

        and `payment_way` = #{paymentWay}


        and `order_state` = #{orderState}

    AND deleted = 0

SELECT * FROM `order`

    1 = 1

        and `user_id` = #{args.userId}


        and `payment_way` = #{args.paymentWay}


        and `order_state` = #{args.orderState}

    AND deleted = 0

ORDER BY `create_date` DESC
